mapper.select:
select u.*,c.name cname ,c.price,c.description
from user_shops_rela u LEFT JOIN squirrel_commodity c on u.shop_id = c.id where u.user_id = #{userId,jdbcType=INTEGER}
ORDER BY creation_time DESC

select u.*,c.name cname ,c.price,c.description
from user_shops_rela u LEFT JOIN squirrel_commodity c on u.shop_id = c.id where u.shop_id = #{shopId,jdbcType=INTEGER}

  and status=#{status,jdbcType=TINYINT}

ORDER BY creation_time DESC
